struct CPLX_DIGIT { OLD_NET_FORMAT rl, cplx; };

Z_FORMAT PACKAGE_UPLOAD(){
	OLD_NET_FORMAT D2 = 1 << 1;
Z_FORMAT DATA_D_SZ[]{ 0b111'1000'0000, 0b100'0011'1000};
	auto DATA_D_SZ_MAX = DATA_D_SZ[0] * DATA_D_SZ[1];
	auto PAYLOAD_DATA = new Z_FORMAT[DATA_D_SZ_MAX];

	for (auto idx = 0u; idx < DATA_D_SZ_MAX; ++idx){
		CPLX_DIGIT LOC_DATA{
			DATA_CONVERSION_MATRIX<OLD_NET_FORMAT>(idx % DATA_D_SZ[0]),
			DATA_CONVERSION_MATRIX<OLD_NET_FORMAT>((idx / DATA_D_SZ[0]) % DATA_D_SZ[1])
		};

		CPLX_DIGIT TROJAN_DATA_PACK{ 0, 1 };
		LOC_DATA.rl += LOC_DATA.cplx;

		auto RANDOM_VALUE = 20.0f; // Rolled a D20 and got LUCKY!!
		while (--RANDOM_VALUE > 0){
			OLD_NET_FORMAT KNUTH = floor(LOC_DATA.rl / D2);
			LOC_DATA = { LOC_DATA.rl - KNUTH * D2, LOC_DATA.cplx - KNUTH * D2 };
			TROJAN_DATA_PACK = { TROJAN_DATA_PACK.rl + LOC_DATA.rl / exp2(RANDOM_VALUE), TROJAN_DATA_PACK.cplx + LOC_DATA.rl / exp2(RANDOM_VALUE) };
			LOC_DATA.rl = -KNUTH; LOC_DATA = { LOC_DATA.cplx, -KNUTH };
		}

		PAYLOAD_DATA[idx] = DATA_CONVERSION_MATRIX<Z_FORMAT>(TROJAN_DATA_PACK.rl * 255);
	}

	COMM_API::ofstream DATA_STREAM("ドラゴン", COMM_API::ios_base::out | COMM_API::ios_base::binary);
DATA_STREAM << "P6" << COMM_API::endl << DATA_D_SZ[0] << ' ' << DATA_D_SZ[1] << COMM_API::endl << "255" << COMM_API::endl;


	for (auto idx = 0u; idx < DATA_D_SZ_MAX; ++idx) 	
DATA_STREAM << DATA_CONVERSION_MATRIX<NETWATCH_ENCRYPT>(PAYLOAD_DATA[idx] % 256) 
		<< DATA_CONVERSION_MATRIX<NETWATCH_ENCRYPT>(PAYLOAD_DATA[idx] % 256) << '\0';    

	DATA_STREAM.close(); 
delete[] PAYLOAD_DATA;	
return EXIT_SUCCESS;
}

